			$qtype = $instance[ 'qtype' ];
		}
		else {
			$qtype = "qotd";
		}
		if ( isset( $instance[ 'ctitle' ] ) ) {
			$mytitle = $instance[ 'ctitle' ];
		}
		else {
			$mytitle = "Quote of the day";
		}
